    private static Result floorCount() {
        int result = 0;
        Result res = new Result(null, null);
        for (int i = 0; i < INPUT.length(); i++) {
            char ch = INPUT.charAt(i);
            if (ch == '(') {
                result =result+1;
            }
            else if(ch == ')') {
                result = result -1;
            }

            if (result == -1 && Objects.isNull(res.positionReachedNegative())) {
                 res = new Result(null, i+1);
            }
        }

        return   new Result(result, res.positionReachedNegative());
    }



